mapper.select:
SELECT loan.*
FROM loan loan
WHERE  loan_no = #{value}

SELECT A.* , (Select name from user_info Where user_no = A.duty_staff_no) as duty_user_name , (Select name from user_info Where user_no = A.finish_user_no) as finishUserName , (Select name from user_info Where user_no = A.request_user_no) as requestUserName , (Select name from town Where town_no = A.town_no) as town_name , (Select name from district Where district_no = A.district_no) as district_name , (Select province_name from province Where province_no = A.province_no) as province_name , IFNULL((select COUNT(loan_no) from loan_detail where loan_no = A.loan_no and status = 'P' and confirm_date IS NOT NULL), 0) as alreadyPaidSession , (select IFNULL(SUM(amount), 0) from loan_detail where loan_no = A.loan_no and status = 'P' and confirm_date IS NOT NULL) as paid , (select SUM(capital) from loan_detail where loan_no = A.loan_no and confirm_date IS NULL) as currentDebt , (select IFNULL(SUM(amount), 0) from loan_detail where loan_no = A.loan_no and confirm_date IS NULL and end_date <= DATE(#{currentDate}) and A.status != 'F') as delay_amount , (select SUM(amount) from loan_detail where loan_no = A.loan_no) as totalPaidAmount FROM (
SELECT * FROM ( SELECT L.* , U.name staff_user_name , cust.name customerName , cust.customer_code , cust.town_no town_no , cust.district_no , cust.province_no , (select COUNT(loan_no) from loan_detail where loan_no = L.loan_no) as totalSession , IFNULL((select COUNT(loan_no) from loan_detail where loan_no = L.loan_no and confirm_date IS NULL and end_date <= DATE(#{currentDate}) and L.status != 'F'), 0) as delay_days FROM loan L LEFT OUTER JOIN customer cust ON cust.customer_no = L.customer_no LEFT OUTER JOIN user_info U ON U.user_no = L.staff_user_no ) L
ORDER BY L.reg_date DESC
LIMIT #{startIndex}, #{pageSize}
) A

sql.where:
AND (L.delete_yn IS NULL OR L.delete_yn = 0)

    AND MONTH(L.reg_date) = MONTH(CURRENT_DATE())


    AND L.staff_user_no = #{staffUserNo}


    AND L.town_no = #{townNo}


    AND L.customer_no = #{customerNo}


    AND L.loan_pay_type = #{loanPayType}


    AND L.customer_code LIKE CONCAT('%', #{customerCode}, '%')


    AND L.totalSession  > L.alreadyPaidSession





            AND L.delay_days > 0


            AND L.delay_days = 0






            AND L.status IN ('N', 'A')


            AND L.status = #{status}




    AND CONCAT(L.customerName ,L.customer_code, L.contract_code) LIKE CONCAT('%',#{searchText},'%')

mapper.select:
SELECT A.* , (Select name from user_info Where user_no = A.duty_staff_no) as duty_user_name , IFNULL((select COUNT(loan_no) from loan_detail where loan_no = A.loan_no and confirm_date IS NULL and end_date <= DATE(#{currentDate}) and A.status != 'F'), 0) as delay_days , (select SUM(capital) from loan_detail where loan_no = A.loan_no and confirm_date IS NULL) as currentDebt
FROM (
SELECT * FROM ( SELECT L.* , U.name staff_user_name , cust.name customerName , cust.customer_code , cust.town_no town_no , (select COUNT(loan_no) from loan_detail where loan_no = L.loan_no) as totalSession , IFNULL((select COUNT(loan_no) from loan_detail where loan_no = L.loan_no and status = 'P' and confirm_date IS NOT NULL), 0) as alreadyPaidSession FROM loan L LEFT OUTER JOIN customer cust ON cust.customer_no = L.customer_no LEFT OUTER JOIN user_info U ON U.user_no = L.staff_user_no
) L
ORDER BY L.reg_date DESC
LIMIT #{startIndex}, #{pageSize}
) A
