mapper.select:
SELECT * ,moduleCertCompinfo1.cert_compnm mobileVerifyServiceCompName ,moduleCertCompinfo2.cert_compnm ipinVerifyServiceCompName FROM module_signup moduleSignup LEFT JOIN module_cert_compinfo moduleCertCompinfo1 ON moduleSignup.mobile_verify_service = moduleCertCompinfo1.cert_compcd AND moduleCertCompinfo1.cert_type = 'S' LEFT JOIN module_cert_compinfo moduleCertCompinfo2 ON moduleSignup.ipin_verify_service = moduleCertCompinfo2.cert_compcd AND moduleCertCompinfo2.cert_type = 'I'
ORDER BY moduleSignup.reg_date DESC
LIMIT #{startIndex}, #{pageSize}
